mapper.select:
	select task_name , timezong as time from (
select *,sum(time) timezong from (select * from record where to_days(great_time) = to_days(now())) c group by task_name

)b
where user_name= #{userName}

select task_name , timezong as time from (
select *,sum(time) timezong from (select * from record where TO_DAYS(now()) - TO_DAYS(great_time)  <=7 ) c group by task_name

)b
where user_name= #{userName}
order by time DESC
limit 0,10
